function generateMessage(category) {
    const messagesArray = messages[category];
    const messageContainer = document.getElementById(`${category}Messages`);
    
    // Clear previous messages
    messageContainer.innerHTML = '';

    // Generate new message and add to the container
    const message = messagesArray[Math.floor(Math.random() * messagesArray.length)];
    const messageElement = document.createElement('p');
    messageElement.textContent = message;
    messageContainer.appendChild(messageElement);
}

function copyMessage(category) {
    const messageContainer = document.getElementById(`${category}Messages`);
    const textToCopy = Array.from(messageContainer.children)
        .map(child => child.textContent)
        .join('\n');

    navigator.clipboard.writeText(textToCopy).then(() => {
        // Optionally, you can add visual feedback for copying if desired
        // e.g., alert('Message copied to clipboard');
    });
}
